select 

from commodity


    and commodity_no = #{commodityNo,jdbcType=VARCHAR}


    and commodity_name = #{commodityName,jdbcType=VARCHAR}


    and title = #{title,jdbcType=VARCHAR}


    and is_promotion = #{isPromotion,jdbcType=VARCHAR}


    and price = #{price,jdbcType=DECIMAL}


    and promotion_price = #{promotionPrice,jdbcType=VARCHAR}


    and volume = #{volume,jdbcType=BIGINT}


    and evaluate = #{evaluate,jdbcType=VARCHAR}


    and size = #{size,jdbcType=VARCHAR}


    and fk_industry_id = #{fkIndustryId,jdbcType=BIGINT}


    and fk_secondary_industry_id = #{fkSecondaryIndustryId,jdbcType=BIGINT}


    and fk_classification_id = #{fkClassificationId,jdbcType=BIGINT}


    and integral = #{integral,jdbcType=BIGINT}


    and create_id = #{createId,jdbcType=BIGINT}


    and to_days(create_time) = to_days(#{createTime,jdbcType=TIMESTAMP})


    and to_days(update_time) = to_days(#{updateTime,jdbcType=TIMESTAMP})


    and is_del = #{isDel,jdbcType=VARCHAR}

