select 

from industry_secondary_relation


    and fk_industry_id = #{fkIndustryId,jdbcType=BIGINT}


    and fk_secondary_industry_id = #{fkSecondaryIndustryId,jdbcType=BIGINT}


    and fk_classification_id = #{fkClassificationId,jdbcType=BIGINT}


    and create_id = #{createId,jdbcType=BIGINT}


    and to_days(create_time) = to_days(#{createTime,jdbcType=TIMESTAMP})


    and to_days(update_time) = to_days(#{updateTime,jdbcType=TIMESTAMP})


    and is_del = #{isDel,jdbcType=VARCHAR}

