select 

from merchant


    and merchants_no = #{merchantsNo,jdbcType=VARCHAR}


    and name = #{name,jdbcType=VARCHAR}


    and fk_client_id = #{fkClientId,jdbcType=BIGINT}


    and money = #{money,jdbcType=DECIMAL}


    and no_withdrawal_money = #{noWithdrawalMoney,jdbcType=DECIMAL}


    and cash_advance_money = #{cashAdvanceMoney,jdbcType=DECIMAL}


    and frozen_money = #{frozenMoney,jdbcType=DECIMAL}


    and guarantee_money = #{guaranteeMoney,jdbcType=DECIMAL}


    and merchants_level = #{merchantsLevel,jdbcType=VARCHAR}


    and desc_score = #{descScore,jdbcType=VARCHAR}


    and service_score = #{serviceScore,jdbcType=VARCHAR}


    and logistics_score = #{logisticsScore,jdbcType=VARCHAR}


    and create_id = #{createId,jdbcType=BIGINT}


    and to_days(create_time) = to_days(#{createTime,jdbcType=TIMESTAMP})


    and to_days(update_time) = to_days(#{updateTime,jdbcType=TIMESTAMP})


    and is_del = #{isDel,jdbcType=VARCHAR}

