select 

from order_commodity_relation


    and fk_base_order_id = #{fkBaseOrderId,jdbcType=BIGINT}


    and fk_commodity_id = #{fkCommodityId,jdbcType=BIGINT}


    and number = #{number,jdbcType=BIGINT}


    and money = #{money,jdbcType=DECIMAL}


    and discount = #{discount,jdbcType=DECIMAL}


    and is_settlement = #{isSettlement,jdbcType=VARCHAR}


    and fk_logistics_records_id = #{fkLogisticsRecordsId,jdbcType=BIGINT}


    and create_id = #{createId,jdbcType=BIGINT}


    and to_days(create_time) = to_days(#{createTime,jdbcType=TIMESTAMP})


    and to_days(update_time) = to_days(#{updateTime,jdbcType=TIMESTAMP})


    and is_del = #{isDel,jdbcType=VARCHAR}

